package store

import (
	"context"
	"crypto/ecdsa"
	"encoding/hex"
	"log"
	"math/big"
	"time"

	"github.com/ethereum/go-ethereum"
	"github.com/ethereum/go-ethereum/common"
	"github.com/ethereum/go-ethereum/core/types"
	"github.com/ethereum/go-ethereum/crypto"
	"github.com/ethereum/go-ethereum/ethclient"
)

// 使用ethclient部署store合约的示例
//定义合约字节码常量（获取Store_sol.bin中的二进制字符串）

const (
	//store合约字节码
	contractByCode = ""
)

func DeployStoreTestByEthclient() {
	//链接测试环境获取客户端
	client, err := ethclient.Dial("https://eth-sepolia.g.alchemy.com/v2/rGBqI4PDZG8o5pjuB0U98")
	if err != nil {
		log.Fatal(err)
	}

	//创建私钥
	privateKey, err := crypto.HexToECDSA("085d06eb0e8f9733067a4734a7fec9a9c88ccce1bc4bf375b5c208fc523cd70c")
	if err != nil {
		log.Fatal(err)
	}

	//获取公钥
	publicKey := privateKey.Public()

	//使用类型断言转换为 *ecdsa.PublicKey 类型
	publicKeyECDSA, ok := publicKey.(*ecdsa.PublicKey)
	if !ok {
		log.Fatal("cannot assert type: publicKey is not of type *ecdsa.PublicKey")
	}

	//获取地址
	fromAddress := crypto.PubkeyToAddress(*publicKeyECDSA)

	//获取账户nonce值
	nonce, err := client.PendingNonceAt(context.Background(), fromAddress)
	if err != nil {
		log.Fatal(err)
	}
	//设置交易的gas价格
	gasPrice, err := client.SuggestGasPrice(context.Background())
	if err != nil {
		log.Fatal(err)
	}

	//解码合约字节码
	data, err := hex.DecodeString(contractByCode)
	if err != nil {
		log.Fatal(err)
	}
	//创建交易
	tx := types.NewContractCreation(nonce, big.NewInt(0), 3000000, gasPrice, data)

	//对交易进行签名
	chainID, err := client.NetworkID(context.Background())
	if err != nil {
		log.Fatal(err)
	}
	signedTx, err := types.SignTx(tx, types.NewEIP155Signer(chainID), privateKey)
	if err != nil {
		log.Fatal(err)
	}

	//发送交易
	err = client.SendTransaction(context.Background(), signedTx)
	if err != nil {
		log.Fatal(err)
	}

	log.Printf("tx sent,生成的交易的哈希: %s", signedTx.Hash().Hex())

	//等待交易被挖矿
	receipt, err := waitForReceipt(client, signedTx.Hash())
	if err != nil {
		log.Fatal(err)
	}
	log.Printf("contract deployed at address: %s", receipt.ContractAddress.Hex())

}

/*
*

	client.TransactionReceipt(context.Background(), txHash)
	作用：获取交易的收据（Receipt），包含交易的执行结果和详细信息。

	主要使用场景：

		检查交易是否成功（最重要的用途）
		获取交易消耗的 Gas
		查看智能合约事件日志
		确认交易是否被打包进区块
		获取交易所在的区块信息

	典型使用场景：

		发送转账后，等待并确认是否成功
		调用智能合约后，查看执行结果和触发的事件
		监控交易状态（pending → confirmed → success/failed）
		计算交易实际花费（GasUsed × GasPrice）
		简单理解：交易收据就像交易的"体检报告"，告诉你交易执行后的所有结果。
*/
func waitForReceipt(client *ethclient.Client, txHash common.Hash) (*types.Receipt, error) {
	for {
		receipt, err := client.TransactionReceipt(context.Background(), txHash)
		if err == nil {
			return receipt, nil
		}
		if err != ethereum.NotFound {
			return nil, err
		}
		// 等待一段时间后再次查询
		time.Sleep(1 * time.Second)
	}
}
